import { getProducts } from "../api.js";
import "./App.css";
import ProductList from "./ProductList.js";
import { useState, useEffect } from "react";

const LIMIT = 6;

function App() {
  const [items, setItems] = useState([]);
  const [offset, setOffset] = useState(0);
  const [order, setOrder] = useState("createdAt");
  const [hasNext, setHasNext] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [loadingError, setLoadingError] = useState(null);

  const sortedItems = items.sort((a, b) => b[order] - a[order]);

  const handleNewestClick = () => setOrder("createdAt");
  const handleBestClick = () => setOrder("favoriteCount");
  const handleDelete = (id) => {
    const nextItems = items.filter((item) => item.id !== id);
    setItems(nextItems);
  };

  const handleLoad = async (options) => {
    let result;
    try {
      setIsLoading(true);
      setLoadingError(null);
      result = await getProducts(options);
    } catch (error) {
      setLoadingError(error);
      return;
    } finally {
      setIsLoading(false);
    }

    const { reviews, paging } = result;
    if (options.offset === 0) {
      setItems(reviews);
    } else {
      setItems((previewItems) => [...previewItems, ...reviews]);
    }
    setOffset(options.offset + options.limit);
    setHasNext(paging.hasNext);
  };

  const handleLoadMore = () => {
    handleLoad({ order, offset, limit: LIMIT });
  };

  useEffect(() => {
    handleLoad({ order, offset: 0, limit: LIMIT });
  }, [order]);

  return (
    <div>
      <p>판다마켓 로고</p>
      <a href="#">자유게시판</a>
      <a href="#"> 중고마켓</a>
      <button>로그인</button>
      <div>
        <div>
          <h1>베스트 상품</h1>
          <p>베스트 상품 섹션 만들어서 넣을거임</p>
        </div>
        <h1>판매중인 상품</h1>
        <input placeholder="검색할 상품을 입력하세요"></input>
        <button>상품 등록하기</button>
        <button onClick={handleNewestClick}>최신순</button>
        <button onClick={handleBestClick}>좋아요순</button>
      </div>
      <ProductList items={sortedItems} />
      {hasNext && (
        <button disabled={isLoading} onClick={handleLoadMore}>
          더보기
        </button>
      )}
      {loadingError?.message && <span>{loadingError?.message}</span>}
    </div>
  );
}

export default App;
