import getProducts from "../api";

const product = getProducts.list.map((items) => ({
  id: items.id,
  title: items.name,
  description: items.description,
  price: items.price,
  image: items.images,
  favoriteCount: items.favoriteCount,
  createAt: items.createAt,
}));

function ProductListItem({ item }) {
  const { title, price, image, favoriteCount } = item;
  return (
    <div className="product-list-item">
      <img className="image" src={image} alt={title}></img>
      <div className="title">{title}</div>
      <div className="price">{price}원</div>
      <div className="like">
        <img className="like-icon" src={likeIcon} alt="좋아요 아이콘"></img>
        <div className="like-num">{favoriteCount}</div>
      </div>
    </div>
  );
}

function ProductList({ items }) {
  return (
    <li key={items.id}>
      <ProductListItem item={product} />
    </li>
  );
}

export default ProductList;
