import { useEffect, useState } from "react";
import { getProducts } from "../api.js";
import "./App.css";
import ProductList from "./ProductList.js";
import BestList from "./BestList.js";
import Nav from "./Nav.js";

const LIMIT = 10;

function App() {
  const [items, setItems] = useState([]);
  const [offset, setOffset] = useState(0);
  const [order, setOrder] = useState("createdAt");
  const [hasNext, setHasNext] = useState(false);

  const handleNewestClick = () => setOrder("createdAt");
  const handleBestClick = () => setOrder("rating");

  async function handleLoad({ order, offset, limit: LIMIT }) {
    let result = await getProducts({ order, offset, limit: LIMIT });
    const { paging, reviews } = result;

    if (offset === 0) {
      setItems(reviews);
    } else {
      setItems((prevItems) => [...prevItems, ...reviews]);
    }

    setOffset(offset + LIMIT);
    setHasNext(paging.hasNext);
  }

  const handleLoadMore = () => {
    handleLoad({ order, offset, limit: LIMIT });
  };

  useEffect(() => {
    handleLoad({ order, offset: 0, limit: LIMIT });
  }, [order]);

  console.log(items);

  return (
    <div className="full-body">
      <Nav />
      <div className="main">
        <div>
          <h1>베스트 상품</h1>
          <BestList items={items} />
        </div>
        <div>
          <h1>판매중인 상품</h1>
          <input placeholder="검색할 상품을 입력하세요"></input>
          <button>상품 등록하기</button>
          <button onClick={handleNewestClick}>최신순</button>
          <button onClick={handleBestClick}>좋아요순</button>
        </div>
        <ProductList items={items} />
        {hasNext && <button onClick={handleLoadMore}>다음 페이지</button>}
      </div>
    </div>
  );
}

export default App;
