import { useEffect, useState } from "react";
import { getProducts } from "../api.js";
import "./App.css";
import ProductList from "./ProductList.js";
import BestList from "./BestList.js";
import Nav from "./Nav.js";

const LIMIT = 10;

function App() {
  const [items, setItems] = useState([]);
  const [offset, setOffset] = useState(0);
  const [order, setOrder] = useState("createdAt");
  const [hasNext, setHasNext] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [loadingError, setLoadingError] = useState(null);

  const handleNewestClick = () => setOrder("createdAt");
  const handleBestClick = () => setOrder("rating");

  async function handleLoad(options) {
    try {
      setIsLoading(true);
      setLoadingError(null);
      const result = await getProducts();
      setItems(result);
    } catch (error) {
      console.error("Error fetching products:", error);
    } finally {
      setIsLoading(false);
    }
    const { reviews, paging } = result;
    if (options.offset === 0) {
      setItems(reviews);
    } else {
      setItems((previewItems) => [...previewItems, ...reviews]);
    }
    setOffset(options.offset + options.limit);
    setHasNext(paging.hasNext);
  }

  const handleLoadMore = () => {
    handleLoad({ order, offset, limit: LIMIT });
  };

  useEffect(() => {
    handleLoad({ order, offset: 0, limit: LIMIT });
  }, []);

  return (
    <div className="full-body">
      <Nav />
      <div className="main">
        <div>
          <h1>베스트 상품</h1>
          <BestList items={items} />
        </div>
        <div>
          <h1>판매중인 상품</h1>
          <input placeholder="검색할 상품을 입력하세요"></input>
          <button>상품 등록하기</button>
          <button onClick={handleNewestClick}>최신순</button>
          <button onClick={handleBestClick}>좋아요순</button>
        </div>
        <ProductList items={items} />
        <button onClick={handleLoadMore}>다음 페이지</button>
      </div>
    </div>
  );
}

export default App;
